import React, { useState, useEffect, useMemo } from 'react';
import Papa from 'papaparse';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { Search, BookOpen, TrendingUp, ChevronRight, X, Loader, FileText } from 'lucide-react';

// ===================================================================================
// UTILITY FUNCTIONS
// ===================================================================================

const fetchAndParseCSV = (filePath) => {
  return new Promise((resolve, reject) => {
    Papa.parse(filePath, {
      download: true,
      header: true,
      skipEmptyLines: true,
      // BUG FIX: Force all headers to lowercase to prevent case-sensitivity issues.
      transformHeader: header => header.trim().toLowerCase(),
      transform: (value, header) => {
        const trimmedValue = value ? value.trim() : '';
        // BUG FIX: Check against lowercase header.
        if (header === 'frequency' || header === 'freq') {
          return parseInt(trimmedValue, 10) || 0;
        }
        return trimmedValue;
      },
      complete: (results) => {
        if (results.errors.length) console.warn(`Errors parsing ${filePath}:`, results.errors);
        resolve(results.data);
      },
      error: (error) => reject(error)
    });
  });
};

const parseFileName = (filename) => {
  const match = filename.match(/^([A-Z]+)(\d+[a-z]?)_([A-Z]+)\.csv$/);
  if (match) {
    const [, childName, ageSession, speakerCode] = match;
    let ageInfo = ageSession;
    let ageInDays = 0;
    const ageMatch = ageSession.match(/^(\d)(\d{2})(\d{2})([a-z]?)$/);
    if (ageMatch) {
      const [, years, months, days, suffix] = ageMatch;
      ageInfo = `${years}y${parseInt(months, 10)}m${parseInt(days, 10)}d${suffix}`;
      ageInDays = parseInt(years, 10) * 365 + parseInt(months, 10) * 30 + parseInt(days, 10);
    }
    const speakerMap = { 'CHI': 'Child', 'MOT': 'Mother', 'REL': 'Relative', 'CHA': 'Caregiver' };
    const speakerType = speakerMap[speakerCode] || speakerCode;
    return {
      filename, participant: childName, session: ageSession, ageInfo, ageInDays,
      speaker: speakerType, speakerCode, isChild: speakerCode === 'CHI',
      fullLabel: `${childName} (${speakerType}) - ${ageInfo}`
    };
  }
  return { filename, participant: 'Unknown', speaker: 'Unknown', session: filename, ageInDays: 0 };
};

const getChildColor = (childName) => {
  const PREDEFINED_COLORS = { 'ALE': '#3b82f6', 'ANA': '#ef4444', 'GAB': '#10b981', 'MAT': '#f59e0b' };
  const FALLBACK_COLORS = ['#8b5cf6', '#ec4899', '#64748b'];
  if (PREDEFINED_COLORS[childName]) return PREDEFINED_COLORS[childName];
  const charCodeSum = childName.split('').reduce((sum, char) => sum + char.charCodeAt(0), 0);
  return FALLBACK_COLORS[charCodeSum % FALLBACK_COLORS.length];
};


// ===================================================================================
// UI COMPONENTS
// ===================================================================================

const LoadingView = () => ( <div className="h-full flex flex-col justify-center items-center text-center p-8 bg-gray-50"><Loader size={48} className="text-indigo-500 animate-spin mb-4" /><h2 className="text-xl font-semibold text-gray-700">Loading Corpus Data...</h2></div>);
const WelcomeView = ({ stats, fileCount }) => ( <div className="h-full flex flex-col justify-center items-center text-center p-8 bg-gray-50"><BookOpen size={48} className="text-gray-300 mb-4" /><h2 className="text-2xl font-semibold text-gray-700">Georgian Child Corpus Explorer</h2><p className="text-gray-500 max-w-md mt-2 mb-6">Use the controls on the left to search the corpus.</p>{stats && ( <div className="grid grid-cols-2 gap-x-8 gap-y-4 text-left p-6 border rounded-lg bg-white shadow-sm"><div className="font-semibold text-gray-600">Files Loaded:</div><div className="text-gray-900 font-mono">{fileCount}</div><div className="font-semibold text-gray-600">Total Occurrences:</div><div className="text-gray-900 font-mono">{stats.totalOccurrences.toLocaleString()}</div><div className="font-semibold text-gray-600">Unique Words:</div><div className="text-gray-900 font-mono">{stats.uniqueWords.toLocaleString()}</div><div className="font-semibold text-gray-600">Unique Lemmas:</div><div className="text-gray-900 font-mono">{stats.uniqueLemmas.toLocaleString()}</div></div>)}</div>);
const FilterSection = ({ title, options, selected, onToggle, onSelectAll, onDeselectAll }) => ( <div className="mb-4"><div className="flex justify-between items-center mb-1"><h4 className="text-xs font-semibold text-gray-500 uppercase tracking-wider">{title}</h4><div><button onClick={onSelectAll} className="text-xs text-indigo-600 hover:text-indigo-800 font-medium mr-2">All</button><button onClick={onDeselectAll} className="text-xs text-gray-500 hover:text-gray-700 font-medium">None</button></div></div><div className="space-y-1 text-sm">{options.map(option => ( <label key={option} className="flex items-center space-x-2 cursor-pointer"><input type="checkbox" checked={selected.includes(option)} onChange={() => onToggle(option)} className="h-4 w-4 rounded border-gray-300 text-indigo-600 focus:ring-indigo-500" /><span>{option}</span></label>))}</div></div>);
const ControlPanel = ({searchTerm, setSearchTerm, searchBy, setSearchBy, filters, setFilters, availableFiles, selectedFiles, setSelectedFiles}) => { const allParticipants = useMemo(() => [...new Set(availableFiles.map(f => f.participant))], [availableFiles]); const allSpeakers = useMemo(() => [...new Set(availableFiles.map(f => f.speaker))], [availableFiles]); const handleFilterChange = (type, value) => { setFilters(prev => ({...prev, [type]: prev[type].includes(value) ? prev[type].filter(v => v !== value) : [...prev[type], value]})); }; return ( <div className="p-4 border-r border-gray-200 bg-gray-50/50 flex flex-col h-full overflow-y-auto"><div className="relative mb-4"><Search className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" size={20} /><input type="text" placeholder="Search..." value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500" /></div><div className="mb-6"><h3 className="text-sm font-semibold text-gray-600 mb-2">Search By</h3><div className="flex flex-wrap gap-x-4 gap-y-2 text-sm">{['Word', 'Lemma', 'Morphemes', 'Gloss'].map(option => ( <label key={option} className="flex items-center space-x-2 cursor-pointer"><input type="radio" name="searchBy" value={option} checked={searchBy === option} onChange={(e) => setSearchBy(e.target.value)} className="h-4 w-4 text-indigo-600 border-gray-300 focus:ring-indigo-500" /><span>{option}</span></label>))}</div></div><FilterSection title="Participants" options={allParticipants} selected={filters.participants} onToggle={(val) => handleFilterChange('participants', val)} onSelectAll={() => setFilters(f => ({ ...f, participants: allParticipants }))} onDeselectAll={() => setFilters(f => ({ ...f, participants: [] }))} /><FilterSection title="Speakers" options={allSpeakers} selected={filters.speakers} onToggle={(val) => handleFilterChange('speakers', val)} onSelectAll={() => setFilters(f => ({ ...f, speakers: allSpeakers }))} onDeselectAll={() => setFilters(f => ({ ...f, speakers: [] }))} /></div>); };

// BUG FIX: Use lowercase properties (e.g., item.word)
const ResultsList = ({ results, onSelectItem }) => ( <div className="flex flex-col h-full bg-white"><div className="p-4 border-b border-gray-200 bg-gray-50"><p className="text-sm text-gray-600 font-semibold">{results.length.toLocaleString()} occurrences found</p></div><div className="overflow-y-auto">{results.length > 0 ? ( <ul>{results.map((item) => ( <li key={item.occurrenceId}><button onClick={() => onSelectItem(item)} className="w-full text-left p-4 border-b border-gray-200 hover:bg-indigo-50"><div className="flex justify-between items-center"><div className="flex-1 overflow-hidden"><p className="font-semibold text-indigo-800 truncate">{item.word}</p><p className="text-sm text-gray-600 truncate">{item.gloss}</p><div className="flex items-center space-x-2 mt-2 text-xs"><span className="bg-gray-200 text-gray-700 px-2 py-0.5 rounded-full font-medium">{item.participant}</span><span className={`px-2 py-0.5 rounded-full font-medium ${item.isChild ? 'bg-blue-100 text-blue-700' : 'bg-green-100 text-green-700'}`}>{item.speaker}</span><span className="text-gray-500">{item.ageInfo}</span></div></div><ChevronRight className="text-gray-400 ml-4 shrink-0" size={20} /></div></button></li>))}</ul>) : ( <div className="p-8 text-center text-gray-500 flex flex-col items-center justify-center h-full"><FileText size={40} className="text-gray-300 mb-4" /><p className="font-semibold">No Occurrences Found</p></div>)}</div></div>);
const DevelopmentalChart = ({ childName, chartData, color }) => { const CustomTooltip = ({ active, payload, label }) => { if (active && payload && payload.length) { return ( <div className="bg-white/80 backdrop-blur-sm border border-gray-200 rounded-lg shadow-lg p-3 text-sm"><p className="font-bold mb-2">{`Age: ${label}`}</p>{payload.map((entry, index) => ( <p key={index} style={{ color: entry.color }} className="font-medium">{entry.name === 'Child' ? `${childName} (Child): ${entry.value}` : `Adults: ${entry.value}`}</p>))}</div>); } return null; }; return ( <div className="mb-8 border border-gray-200 rounded-lg p-4 bg-white shadow-sm"><h4 className="text-lg font-semibold text-gray-700 mb-3 flex items-center"><span className="inline-block w-4 h-4 rounded mr-2" style={{ backgroundColor: color }}></span>{childName} - Developmental Timeline</h4><div style={{ width: '100%', height: 350 }}><ResponsiveContainer><LineChart data={chartData} margin={{ top: 5, right: 20, left: 10, bottom: 50 }}><CartesianGrid strokeDasharray="3 3" stroke="#e0e0e0" /><XAxis dataKey="ageLabel" angle={-45} textAnchor="end" height={60} fontSize={12} stroke="#666" interval="preserveStartEnd" /><YAxis fontSize={12} stroke="#666" label={{ value: 'Frequency', angle: -90, position: 'insideLeft', offset: -5 }} allowDecimals={false} /><Tooltip content={<CustomTooltip />} /><Legend /><Line type="monotone" dataKey="child" name="Child" stroke={color} strokeWidth={3} dot={{ r: 4 }} activeDot={{ r: 6 }} /><Line type="monotone" dataKey="adults" name="Adults" stroke="#6b7280" strokeWidth={2} strokeDasharray="5 5" /></LineChart></ResponsiveContainer></div></div>); };

// BUG FIX: Use lowercase properties (e.g., item.word)
const DetailView = ({ item, onClose, frequencyData }) => { const chartDataByChild = useMemo(() => { if (!frequencyData) return {}; const groups = {}; frequencyData.forEach(entry => { if (!groups[entry.participant]) groups[entry.participant] = []; groups[entry.participant].push(entry); }); const childChartData = {}; for (const childName in groups) { const sessionGroups = {}; groups[childName].forEach(entry => { const sessionKey = entry.ageInfo; if (!sessionGroups[sessionKey]) { const ageMatch = entry.ageInfo.match(/^(\d+)y(\d+)m/); sessionGroups[sessionKey] = { ageInfo: sessionKey, ageInDays: entry.ageInDays, ageLabel: ageMatch ? `${ageMatch[1]}y ${ageMatch[2]}m` : entry.ageInfo, child: 0, adults: 0 }; } if (entry.isChild) sessionGroups[sessionKey].child += entry.frequency; else sessionGroups[sessionKey].adults += entry.frequency; }); childChartData[childName] = Object.values(sessionGroups).sort((a, b) => a.ageInDays - b.ageInDays); } return childChartData; }, [frequencyData]); return ( <div className="h-full overflow-y-auto p-6 bg-gray-50 relative"><button onClick={onClose} className="absolute top-4 right-4 text-gray-500 hover:text-gray-800" aria-label="Close details"><X size={24} /></button><div className="mb-8 p-6 bg-white rounded-lg shadow-sm border border-gray-200"><h2 className="text-3xl font-bold text-gray-900">{item.word}</h2><p className="text-lg text-gray-600 mt-1">{item.gloss}</p><div className="mt-4 grid grid-cols-2 gap-x-4 gap-y-2 text-sm"><p className="text-gray-500">Lemma: <span className="font-semibold text-gray-700">{item.lemma || 'N/A'}</span></p><p className="text-gray-500">POS: <span className="font-semibold text-gray-700">{item.pos || 'N/A'}</span></p></div>{item.morphemes && ( <div className="mt-4"><p className="text-sm text-gray-500 mb-1">Morphemes:</p><div className="font-mono text-sm bg-gray-100 p-2 rounded">{item.morphemes}</div></div>)}</div>{frequencyData && frequencyData.length > 0 && ( <div><h3 className="text-xl font-semibold text-gray-800 mb-4 flex items-center"><TrendingUp size={20} className="mr-2 text-indigo-500" />Developmental Timelines</h3>{Object.keys(chartDataByChild).map(childName => ( <DevelopmentalChart key={childName} childName={childName} chartData={chartDataByChild[childName]} color={getChildColor(childName)} />))}</div>)}</div>); };

// ===================================================================================
// MAIN APP COMPONENT
// ===================================================================================

export default function CorpusDataApp() {
  const [corpusData, setCorpusData] = useState([]);
  const [corpusStats, setCorpusStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchBy, setSearchBy] = useState('Word');
  const [selectedItem, setSelectedItem] = useState(null);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [filters, setFilters] = useState({ participants: [], speakers: [] });

  useEffect(() => {
    const loadCorpus = async () => {
      try {
        setLoading(true);
        const manifestResponse = await fetch('/manifest.json');
        if (!manifestResponse.ok) throw new Error('Could not load manifest.json');
        const { files: csvFiles } = await manifestResponse.json();
        const dataPromises = csvFiles.map(async (filename) => {
          try {
            const fileInfo = parseFileName(filename);
            const data = await fetchAndParseCSV(`/${filename}`);
            return { fileInfo, data };
          } catch (e) { return null; }
        });
        const loadedData = (await Promise.all(dataPromises)).filter(Boolean);
        setCorpusData(loadedData);

        // BUG FIX: Use lowercase properties for stats calculation.
        const allEntries = loadedData.flatMap(file => file.data);
        setCorpusStats({
          totalOccurrences: allEntries.length,
          uniqueWords: new Set(allEntries.map(item => item.word)).size,
          uniqueLemmas: new Set(allEntries.map(item => item.lemma)).size,
        });

        const fileInfos = loadedData.map(d => d.fileInfo);
        setSelectedFiles(fileInfos.map(f => f.filename));
        setFilters({
          participants: [...new Set(fileInfos.map(f => f.participant))],
          speakers: [...new Set(fileInfos.map(f => f.speaker))],
        });
      } catch (err) { setError(err.message); }
      finally { setLoading(false); }
    };
    loadCorpus();
  }, []);

  const availableFiles = useMemo(() => corpusData.map(cd => cd.fileInfo), [corpusData]);

  const filteredResults = useMemo(() => {
    const lowerSearchTerm = searchTerm.toLowerCase().trim();
    if (!lowerSearchTerm) return [];

    let allOccurrences = [];
    let activeData = corpusData.filter(({ fileInfo }) =>
        selectedFiles.includes(fileInfo.filename) &&
        (filters.participants.length === 0 || filters.participants.includes(fileInfo.participant)) &&
        (filters.speakers.length === 0 || filters.speakers.includes(fileInfo.speaker))
    );

    // BUG FIX: Use lowercase `searchBy` for property access.
    const searchKey = searchBy.toLowerCase();

    if (searchKey === 'lemma') {
      const targetLemmas = new Set();
      activeData.forEach(({ data }) => {
        data.forEach(item => {
          if (item.word && item.word.toLowerCase().includes(lowerSearchTerm)) {
            if (item.lemma) targetLemmas.add(item.lemma);
          }
          if (item.lemma && item.lemma.toLowerCase().includes(lowerSearchTerm)) {
            targetLemmas.add(item.lemma);
          }
        });
      });

      if (targetLemmas.size === 0) return [];

      activeData.forEach(({ fileInfo, data }) => {
        data.forEach(item => {
          if (item.lemma && targetLemmas.has(item.lemma)) {
            allOccurrences.push({ ...item, ...fileInfo, occurrenceId: `${fileInfo.filename}-${item.word}-${Math.random()}` });
          }
        });
      });
    } else {
      activeData.forEach(({ fileInfo, data }) => {
        data.forEach(item => {
          const searchField = item[searchKey] || '';
          if (searchField.toLowerCase().includes(lowerSearchTerm)) {
            allOccurrences.push({ ...item, ...fileInfo, occurrenceId: `${fileInfo.filename}-${item.word}-${Math.random()}` });
          }
        });
      });
    }

    return allOccurrences.sort((a, b) => (b.frequency - a.frequency) || (a.ageInDays - b.ageInDays));
  }, [searchTerm, searchBy, corpusData, selectedFiles, filters]);

  const frequencyDataForDetail = useMemo(() => {
    if (!selectedItem) return [];

    const relevantOccurrences = [];
    corpusData.forEach(({ fileInfo, data }) => {
      // BUG FIX: Use lowercase .word for matching
      const wordEntry = data.find(entry => entry.word === selectedItem.word);
      if (wordEntry && wordEntry.frequency > 0) {
        relevantOccurrences.push({
          ...wordEntry,
          ...fileInfo
        });
      }
    });
    return relevantOccurrences.sort((a, b) => a.participant.localeCompare(b.participant) || a.ageInDays - b.ageInDays);
  }, [selectedItem, corpusData]);

  if (loading) return <LoadingView />;
  if (error) return <div className="p-6 text-red-600 font-bold text-center">Error: {error.toString()}</div>;

  return (
    <div className="h-screen w-screen bg-gray-100 font-sans flex flex-col">
      <header className="bg-white border-b border-gray-200 p-4 shrink-0">
        <h1 className="text-xl font-bold text-gray-800">Georgian Child Corpus Explorer</h1>
      </header>
      <div className="flex-grow flex flex-col md:flex-row overflow-hidden">
        <div className="w-full md:w-80 lg:w-96 shrink-0 h-full">
          <ControlPanel
            searchTerm={searchTerm} setSearchTerm={setSearchTerm}
            searchBy={searchBy} setSearchBy={setSearchBy}
            filters={filters} setFilters={setFilters}
            availableFiles={availableFiles}
            selectedFiles={selectedFiles} setSelectedFiles={setSelectedFiles}
          />
        </div>
        <main className="flex-grow h-full bg-white overflow-hidden">
          {selectedItem ? (
            <DetailView item={selectedItem} onClose={() => setSelectedItem(null)} frequencyData={frequencyDataForDetail} />
          ) : searchTerm ? (
            <ResultsList results={filteredResults} onSelectItem={setSelectedItem} />
          ) : (
            <WelcomeView stats={corpusStats} fileCount={availableFiles.length} />
          )}
        </main>
      </div>
    </div>
  );
}
