    let ageInfo = '';
    if (ageMatch) {
      const [, years, months, days, suffix] = ageMatch;
      ageInfo = `${years}y${parseInt(months)}m${parseInt(days)}d${suffix}`;
    } else {
      ageInfo = ageSession;
    }
    const speakerType = speakerCode === 'CHI' ? 'Child' : speakerCode === 'MOT' ? 'Mother' : speakerCode === 'REL' ? 'Relative' : speakerCode === 'CHA' ? 'Caregiver' : speakerCode;
