import React, { useState, useEffect, useMemo } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { Search, BookOpen, BarChart2, ChevronRight, X, FileText, Loader } from 'lucide-react';

// Utility function to parse frequency CSV data (Word,Frequency format)
const parseFrequencyCSV = (csvText) => {
  const lines = csvText.trim().split('\n');
  const headers = lines[0].split(',').map(h => h.trim());

  return lines.slice(1).map(line => {
    const values = line.split(',');
    const obj = {};
    headers.forEach((header, i) => {
      const value = values[i] ? values[i].trim() : '';
      if (header === 'Frequency' || header === 'Freq') {
        obj['Frequency'] = parseInt(value, 10) || 0;
      } else if (header === 'Word') {
        obj['Word'] = value;
      } else {
        obj[header] = value;
      }
    });
    return obj;
  });
};

// Utility function to parse CSV data
const parseCSV = (csvText) => {
  console.log('Parsing CSV, first 200 chars:', csvText.substring(0, 200));
  const lines = csvText.trim().split('\n');
  console.log('Number of lines:', lines.length);
  const headers = lines[0].split(',').map(h => h.trim());
  console.log('Headers:', headers);

  const data = lines.slice(1).map((line, index) => {
    const values = line.split(',');
    const obj = {};
    headers.forEach((header, i) => {
      const value = values[i] ? values[i].trim() : '';
      if (header === 'Frequency') {
        obj[header] = parseInt(value, 10) || 0;
      } else {
        obj[header] = value;
      }
    });

    // Log first few entries for debugging
    if (index < 3) {
      console.log(`Row ${index}:`, obj);
    }

    return obj;
  });

  console.log('Total parsed entries:', data.length);
  return data;
};

// Extract participant and speaker info from filename
const parseFileName = (filename) => {
  if (filename === 'vocabulary.csv') {
    return { participant: 'ALL', speaker: 'VOC', session: 'vocabulary', type: 'vocabulary' };
  }

  const match = filename.match(/^([A-Z]+)(\d+[a-z]?)_([A-Z]+)\.csv$/);
  if (match) {
    const [, childName, ageSession, speakerCode] = match;

    // Parse age from the session code (e.g., 40123 = 4y01m23d)
    const ageMatch = ageSession.match(/^(\d)(\d{2})(\d{2})([a-z]?)$/);
    let ageInfo = '';
    if (ageMatch) {
      const [, years, months, days, suffix] = ageMatch;
      ageInfo = `${years}y${parseInt(months)}m${parseInt(days)}d${suffix}`;
    } else {
      ageInfo = ageSession;
    }

    const speakerType = speakerCode === 'CHI' ? 'Child' :
                       speakerCode === 'MOT' ? 'Mother' :
                       speakerCode === 'REL' ? 'Relative' :
                       speakerCode === 'CHA' ? 'Caregiver' : speakerCode;

    return {
      participant: childName,
      session: ageSession,
      ageInfo: ageInfo,
      speaker: speakerType,
      speakerCode: speakerCode,
      type: 'frequency',
      displayName: `${childName} (${speakerType})`,
      fullLabel: `${childName} (${speakerType}) - ${ageInfo}`
    };
  }

  return { participant: 'Unknown', speaker: 'Unknown', session: filename, type: 'unknown' };
};

// Control Panel Component
const ControlPanel = ({
  searchTerm,
  setSearchTerm,
  searchBy,
  setSearchBy,
  filters,
  setFilters,
  availableFiles,
  selectedFiles,
  setSelectedFiles
}) => {
  const handleFilterChange = (type, value) => {
    setFilters(prev => {
      const currentValues = new Set(prev[type]);
      if (currentValues.has(value)) {
        currentValues.delete(value);
      } else {
        currentValues.add(value);
      }
      return { ...prev, [type]: Array.from(currentValues) };
    });
  };

  const handleFileToggle = (filename) => {
    setSelectedFiles(prev => {
      if (prev.includes(filename)) {
        return prev.filter(f => f !== filename);
      } else {
        return [...prev, filename];
      }
    });
  };

  const participants = [...new Set(availableFiles.map(f => f.participant))].filter(p => p !== 'ALL');
  const speakers = [...new Set(availableFiles.map(f => f.speaker))].filter(s => s !== 'VOC');

  return (
    <div style={{
      padding: '16px',
      borderRight: '1px solid #e5e7eb',
      backgroundColor: '#f9fafb',
      display: 'flex',
      flexDirection: 'column',
      height: '100%',
      overflowY: 'auto'
    }}>
      {/* Search Bar */}
      <div className="relative mb-4">
        <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" size={20} />
        <input
          type="text"
          placeholder="Search..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition"
        />
      </div>

      {/* Search By Options */}
      <div className="mb-6">
        <h3 className="text-sm font-semibold text-gray-600 mb-2">Search By</h3>
        <div className="space-y-1 text-sm">
          {['Word', 'Lemma', 'Morphemes', 'Gloss'].map(option => (
            <label key={option} className="flex items-center space-x-2 cursor-pointer">
              <input
                type="radio"
                name="searchBy"
                value={option}
                checked={searchBy === option}
                onChange={(e) => setSearchBy(e.target.value)}
                className="h-4 w-4 text-indigo-600 border-gray-300 focus:ring-indigo-500"
              />
              <span>{option}</span>
            </label>
          ))}
        </div>
      </div>

      {/* File Selection */}
      <div className="mb-6">
        <h3 className="text-sm font-semibold text-gray-600 mb-2">Data Files</h3>
        <div className="space-y-2 max-h-40 overflow-y-auto border border-gray-200 rounded p-2">
          {availableFiles.filter(f => f.type === 'frequency').map(file => (
            <label key={file.filename} className="flex items-center space-x-2 cursor-pointer text-sm">
              <input
                type="checkbox"
                checked={selectedFiles.includes(file.filename)}
                onChange={() => handleFileToggle(file.filename)}
                className="h-4 w-4 rounded border-gray-300 text-indigo-600 focus:ring-indigo-500"
              />
              <span>{file.fullLabel || file.displayName}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Filters */}
      <div className="mb-6">
        <h3 className="text-sm font-semibold text-gray-600 mb-2">Filters</h3>

        {/* Participant Filters */}
        <div className="mb-4">
          <h4 className="text-xs font-semibold text-gray-500 mb-1">Participant</h4>
          <div className="space-y-1 text-sm">
            {participants.map(participant => (
              <label key={participant} className="flex items-center space-x-2 cursor-pointer">
                <input
                  type="checkbox"
                  checked={filters.participants.includes(participant)}
                  onChange={() => handleFilterChange('participants', participant)}
                  className="h-4 w-4 rounded border-gray-300 text-indigo-600 focus:ring-indigo-500"
                />
                <span>{participant}</span>
              </label>
            ))}
          </div>
        </div>

        {/* Speaker Filters */}
        <div>
          <h4 className="text-xs font-semibold text-gray-500 mb-1">Speaker Type</h4>
          <div className="space-y-1 text-sm">
            {speakers.map(speaker => (
              <label key={speaker} className="flex items-center space-x-2 cursor-pointer">
                <input
                  type="checkbox"
                  checked={filters.speakers.includes(speaker)}
                  onChange={() => handleFilterChange('speakers', speaker)}
                  className="h-4 w-4 rounded border-gray-300 text-indigo-600 focus:ring-indigo-500"
                />
                <span>{speaker}</span>
              </label>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

// Results List Component
const ResultsList = ({ results, onSelectItem, selectedItem, resultCount, searchBy }) => (
  <div className="flex flex-col h-full bg-white">
    <div className="p-4 border-b border-gray-200 bg-gray-50">
      <p className="text-sm text-gray-600 font-semibold">{resultCount} results found</p>
    </div>
    <div className="overflow-y-auto">
      {results.length > 0 ? (
        <ul>
          {results.map((item, index) => (
            <li key={`${item.Word}-${index}`}>
              <button
                onClick={() => onSelectItem(item)}
                className={`w-full text-left p-4 border-b border-gray-200 hover:bg-indigo-50 transition-colors duration-150 ${
                  selectedItem && selectedItem.Word === item.Word ? 'bg-indigo-100' : ''
                }`}
              >
                <div className="flex justify-between items-center">
                  <div>
                    <p className="font-semibold text-indigo-800">{item.Word}</p>
                    <p className="text-sm text-gray-600">
                      {item.Lemma} • {item.Gloss}
                    </p>
                    {item.Morphemes && (
                      <p className="text-xs text-gray-500 font-mono mt-1">{item.Morphemes}</p>
                    )}
                  </div>
                  <div className="flex items-center space-x-2">
                    <span className="text-sm font-bold text-gray-700 bg-gray-200 rounded-full px-2 py-0.5">
                      {item.Frequency}
                    </span>
                    <ChevronRight className="text-gray-400" size={20} />
                  </div>
                </div>
              </button>
            </li>
          ))}
        </ul>
      ) : (
        <div className="p-6 text-center text-gray-500">
          <p>No results found.</p>
          <p className="text-sm">Try adjusting your search or filters.</p>
        </div>
      )}
    </div>
  </div>
);

// Detail View Component
const DetailView = ({ item, onClose, frequencyData }) => {
  if (!item) return null;

  // Custom color function for bars
  const getBarColor = (entry) => {
    if (entry.isChild) {
      // Different colors for each child
      const childColors = {
        'ALE': '#3b82f6', // Blue
        'ANA': '#ef4444', // Red
        'GAB': '#10b981', // Green
        'MAT': '#f59e0b'  // Orange
      };
      return childColors[entry.participant] || '#6366f1'; // Default purple
    }
    return '#6b7280'; // Gray for adults
  };

  // Custom bar component
  const CustomBar = (props) => {
    const { payload } = props;
    const color = getBarColor(payload);
    return <Bar {...props} fill={color} />;
  };

  return (
    <div className="h-full overflow-y-auto p-6 bg-white relative">
      <button
        onClick={onClose}
        className="absolute top-4 right-4 text-gray-500 hover:text-gray-800"
        aria-label="Close details"
      >
        <X size={24} />
      </button>

      <div className="mb-8">
        <div className="flex items-baseline space-x-4">
          <h2 className="text-2xl font-bold text-gray-900">{item.Word}</h2>
          <span className="text-lg font-semibold text-indigo-600 bg-indigo-100 rounded-full px-3 py-1">
            {item.Frequency}
          </span>
        </div>
        <p className="text-lg text-gray-600 mb-2">{item.Gloss}</p>
        <p className="text-sm text-gray-500">Lemma: <span className="font-semibold">{item.Lemma}</span></p>
        {item.POS && <p className="text-sm text-gray-500">Part of Speech: <span className="font-semibold">{item.POS}</span></p>}
      </div>

      {/* Morphological Analysis */}
      {item.Morphemes && (
        <div className="mb-8">
          <h3 className="text-xl font-semibold text-gray-800 mb-3 border-b pb-2">Morphological Structure</h3>
          <div className="bg-gray-50 p-4 rounded-lg">
            <div className="font-mono text-lg text-center mb-2">
              {item.Morphemes.split('-').map((morpheme, i, arr) => (
                <span key={i}>
                  <span className="bg-blue-100 text-blue-800 px-2 py-1 rounded mx-1">
                    {morpheme}
                  </span>
                  {i < arr.length - 1 && <span className="text-gray-400">-</span>}
                </span>
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Frequency Charts - Separate for Each Child */}
      {frequencyData && frequencyData.length > 0 && (
        <div className="mb-8">
          <h3 className="text-xl font-semibold text-gray-800 mb-4 border-b pb-2 flex items-center">
            <BarChart2 size={20} className="mr-2" />
            Usage Across Age & Speakers
          </h3>

          {/* Legend */}
          <div className="mb-6 flex flex-wrap gap-4">
            <div className="flex items-center space-x-2">
              <div className="w-4 h-4 bg-blue-500 rounded"></div>
              <span className="text-sm font-semibold">Child</span>
            </div>
            <div className="flex items-center space-x-2">
              <div className="w-4 h-4 bg-gray-500 rounded"></div>
              <span className="text-sm font-semibold">Adults (Mother/Relative/Caregiver)</span>
            </div>
          </div>

          {/* Separate chart for each child */}
          {['ALE', 'ANA', 'GAB', 'MAT'].map(childName => {
            const childData = frequencyData.filter(entry => entry.participant === childName);

            if (childData.length === 0) return null;

            // Group by age session to show child and adults together
            const sessionGroups = {};
            childData.forEach(entry => {
              const sessionKey = entry.ageInfo;
              if (!sessionGroups[sessionKey]) {
                sessionGroups[sessionKey] = {
                  ageInfo: sessionKey,
                  ageInDays: entry.ageInDays,
                  child: 0,
                  adults: 0
                };
              }

              if (entry.isChild) {
                sessionGroups[sessionKey].child = entry.frequency;
              } else {
                sessionGroups[sessionKey].adults += entry.frequency;
              }
            });

            const chartData = Object.values(sessionGroups).sort((a, b) => a.ageInDays - b.ageInDays);

            const childColors = {
              'ALE': '#3b82f6', // Blue
              'ANA': '#ef4444', // Red
              'GAB': '#10b981', // Green
              'MAT': '#f59e0b'  // Orange
            };

            return (
              <div key={childName} className="mb-8 border border-gray-200 rounded-lg p-4">
                <h4 className="text-lg font-semibold text-gray-700 mb-3 flex items-center">
                  <span
                    className="inline-block w-4 h-4 rounded mr-2"
                    style={{ backgroundColor: childColors[childName] }}
                  ></span>
                  {childName} - Developmental Timeline
                </h4>

                <div style={{ width: '100%', height: 300 }}>
                  <ResponsiveContainer>
                    <BarChart
                      data={chartData}
                      margin={{ top: 5, right: 20, left: 20, bottom: 60 }}
                    >
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis
                        dataKey="ageInfo"
                        angle={-45}
                        textAnchor="end"
                        height={80}
                        fontSize={12}
                      />
                      <YAxis />
                      <Tooltip
                        formatter={(value, name) => [
                          value,
                          name === 'child' ? `${childName} (Child)` : 'Adults'
                        ]}
                        labelFormatter={(label) => `Age: ${label}`}
                      />
                      <Legend />
                      <Bar
                        dataKey="child"
                        name="Child"
                        fill={childColors[childName]}
                      />
                      <Bar
                        dataKey="adults"
                        name="Adults"
                        fill="#6b7280"
                      />
                    </BarChart>
                  </ResponsiveContainer>
                </div>

                {/* Mini data table for this child */}
                <div className="mt-4 bg-gray-50 rounded p-3">
                  <h5 className="text-sm font-semibold text-gray-600 mb-2">Detailed Usage:</h5>
                  <div className="grid grid-cols-3 gap-4 text-sm">
                    {childData.map((entry, index) => (
                      <div key={index} className="flex justify-between">
                        <span className="text-gray-600">
                          {entry.speaker} ({entry.ageInfo}):
                        </span>
                        <span className="font-semibold text-gray-900">{entry.frequency}</span>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            );
          })}

          {/* Overall Summary Table */}
          <div className="mt-8 overflow-x-auto">
            <h4 className="text-lg font-semibold text-gray-700 mb-3">Complete Usage Summary</h4>
            <table className="min-w-full divide-y divide-gray-200 text-sm">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase">Child</th>
                  <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase">Speaker</th>
                  <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase">Age</th>
                  <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase">Frequency</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {frequencyData.map((entry, index) => (
                  <tr key={index} className="hover:bg-gray-50">
                    <td className="px-4 py-2 font-medium text-gray-900">
                      <span className={`inline-block w-3 h-3 rounded-full mr-2 ${
                        entry.participant === 'ALE' ? 'bg-blue-500' :
                        entry.participant === 'ANA' ? 'bg-red-500' :
                        entry.participant === 'GAB' ? 'bg-green-500' :
                        entry.participant === 'MAT' ? 'bg-orange-500' : 'bg-purple-500'
                      }`}></span>
                      {entry.participant}
                    </td>
                    <td className="px-4 py-2 text-gray-500">
                      <span className={`inline-block w-3 h-3 rounded-full mr-2 ${
                        entry.isChild ? 'bg-blue-400' : 'bg-gray-400'
                      }`}></span>
                      {entry.speaker}
                    </td>
                    <td className="px-4 py-2 text-gray-500 font-mono">{entry.ageInfo}</td>
                    <td className="px-4 py-2 text-gray-900 font-semibold">{entry.frequency}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
};

// Welcome View Component
const WelcomeView = ({ stats, fileCount }) => (
  <div className="h-full flex flex-col justify-center items-center text-center p-8 bg-gray-50">
    <BookOpen size={48} className="text-gray-300 mb-4" />
    <h2 className="text-2xl font-semibold text-gray-700">Georgian Corpus Data Explorer</h2>
    <p className="text-gray-500 max-w-md mt-2 mb-6">
      Explore linguistic data from your corpus files. Use the controls on the left to search and filter the data.
      Select items from the results to see detailed analysis.
    </p>
    <div className="grid grid-cols-2 gap-4 text-left p-4 border rounded-lg bg-white">
      <div className="font-semibold text-gray-600">Files Loaded:</div>
      <div className="text-gray-900 font-mono">{fileCount}</div>
      <div className="font-semibold text-gray-600">Total Entries:</div>
      <div className="text-gray-900 font-mono">{stats.totalEntries.toLocaleString()}</div>
      <div className="font-semibold text-gray-600">Unique Words:</div>
      <div className="text-gray-900 font-mono">{stats.uniqueWords.toLocaleString()}</div>
      <div className="font-semibold text-gray-600">Unique Lemmas:</div>
      <div className="text-gray-900 font-mono">{stats.uniqueLemmas.toLocaleString()}</div>
    </div>
  </div>
);

// Loading Component
const LoadingView = () => (
  <div className="h-full flex flex-col justify-center items-center text-center p-8">
    <Loader size={48} className="text-indigo-500 animate-spin mb-4" />
    <h2 className="text-xl font-semibold text-gray-700">Loading Corpus Data...</h2>
    <p className="text-gray-500 mt-2">Reading CSV files from your corpus...</p>
  </div>
);

// Main App Component
export default function CorpusDataApp() {
  const [allData, setAllData] = useState([]);
  const [availableFiles, setAvailableFiles] = useState([]);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [frequencyFiles, setFrequencyFiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // UI State
  const [searchTerm, setSearchTerm] = useState('');
  const [searchBy, setSearchBy] = useState('Word');
  const [selectedItem, setSelectedItem] = useState(null);
  const [filters, setFilters] = useState({
    participants: [],
    speakers: []
  });

  // Load data from files
  useEffect(() => {
    const loadData = async () => {
      try {
        setLoading(true);

        // Get list of available CSV files
        const csvFiles = [
          'ALE10619_CHI.csv', 'ALE10619_REL.csv', 'ALE20015_CHI.csv', 'ALE20015_REL.csv',
          'ALE20616_CHI.csv', 'ALE20616_MOT.csv', 'ALE20619_CHI.csv', 'ALE20619_REL.csv',
          'ANA30014a_CHI.csv', 'ANA30014a_MOT.csv', 'ANA30614_CHI.csv', 'ANA30614_MOT.csv',
          'ANA40027a_CHI.csv', 'ANA40027a_MOT.csv', 'GAB10600_CHI.csv', 'GAB10600_MOT.csv',
          'GAB10619_CHI.csv', 'GAB10619_MOT.csv', 'GAB20026a_CHI.csv', 'GAB20026a_MOT.csv',
          'MAT30023a_CHA.csv', 'MAT30023a_MOT.csv', 'MAT30620d_CHI.csv', 'MAT30620d_MOT.csv',
          'MAT40121_CHI.csv', 'MAT40121_MOT.csv', 'MAT40123_CHI.csv', 'MAT40123_MOT.csv',
          'vocabulary.csv'
        ];

        const fileInfos = csvFiles.map(filename => ({
          filename,
          ...parseFileName(filename)
        }));

        setAvailableFiles(fileInfos);

        // Load vocabulary.csv as the main dataset
        try {
          const response = await fetch('/vocabulary.csv');
          if (!response.ok) throw new Error('Failed to fetch vocabulary.csv');
          const vocabularyData = await response.text();
          console.log('Raw CSV data:', vocabularyData.substring(0, 200));
          const parsedVocabulary = parseCSV(vocabularyData);
          console.log('Parsed data:', parsedVocabulary.slice(0, 5));
          setAllData(parsedVocabulary);
        } catch (vocabError) {
          console.error('Error loading vocabulary.csv:', vocabError);
          setAllData([]);
        }

        // Load frequency files for distribution analysis
        const frequencyFilePromises = fileInfos
          .filter(f => f.type === 'frequency')
          .map(async (fileInfo) => {
            try {
              const response = await fetch(`/${fileInfo.filename}`);
              if (!response.ok) throw new Error(`Failed to fetch ${fileInfo.filename}`);
              const csvData = await response.text();
              const parsedData = parseFrequencyCSV(csvData);
              return {
                ...fileInfo,
                data: parsedData
              };
            } catch (error) {
              console.warn(`Could not load ${fileInfo.filename}:`, error);
              return null;
            }
          });

        const loadedFrequencyFiles = (await Promise.all(frequencyFilePromises)).filter(Boolean);
        console.log('Loaded frequency files:', loadedFrequencyFiles.length);
        setFrequencyFiles(loadedFrequencyFiles);

        // Initialize selected files (select all by default)
        const frequencyFileNames = fileInfos.filter(f => f.type === 'frequency').map(f => f.filename);
        setSelectedFiles(frequencyFileNames);

        // Initialize filters with all available options
        const participants = [...new Set(fileInfos.map(f => f.participant))].filter(p => p !== 'ALL');
        const speakers = [...new Set(fileInfos.map(f => f.speaker))].filter(s => s !== 'VOC');

        setFilters({
          participants,
          speakers
        });

      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, []);

  // Filter and search data
  const filteredResults = useMemo(() => {
    if (!searchTerm || allData.length === 0) return [];

    const lowerSearchTerm = searchTerm.toLowerCase();

    return allData.filter(item => {
      // Search filter
      let matchesSearch = false;
      switch (searchBy) {
        case 'Word':
          matchesSearch = item.Word && item.Word.toLowerCase().includes(lowerSearchTerm);
          break;
        case 'Lemma':
          matchesSearch = item.Lemma && item.Lemma.toLowerCase().includes(lowerSearchTerm);
          break;
        case 'Morphemes':
          matchesSearch = item.Morphemes && item.Morphemes.toLowerCase().includes(lowerSearchTerm);
          break;
        case 'Gloss':
          matchesSearch = item.Gloss && item.Gloss.toLowerCase().includes(lowerSearchTerm);
          break;
        default:
          matchesSearch = true;
      }

      return matchesSearch;
    }).sort((a, b) => (b.Frequency || 0) - (a.Frequency || 0));
  }, [allData, searchTerm, searchBy, filters]);

  // Calculate frequency data for selected item across files
  const calculateFrequencyData = useMemo(() => {
    if (!selectedItem || frequencyFiles.length === 0) return [];

    const frequencyData = [];

    frequencyFiles
      .filter(file => selectedFiles.includes(file.filename))
      .forEach(file => {
        const wordEntry = file.data.find(entry =>
          entry.Word && entry.Word.toLowerCase() === selectedItem.Word.toLowerCase()
        );

        if (wordEntry && wordEntry.Frequency > 0) {
          // Parse age for sorting
          const ageMatch = file.session.match(/^(\d)(\d{2})(\d{2})([a-z]?)$/);
          let ageInDays = 0;
          if (ageMatch) {
            const [, years, months, days] = ageMatch;
            ageInDays = parseInt(years) * 365 + parseInt(months) * 30 + parseInt(days);
          }

          frequencyData.push({
            source: file.ageInfo,
            frequency: wordEntry.Frequency,
            file: file.filename,
            ageInfo: file.ageInfo,
            speaker: file.speaker,
            participant: file.participant,
            speakerCode: file.speakerCode,
            ageInDays: ageInDays,
            isChild: file.speakerCode === 'CHI',
            label: `${file.participant} (${file.speaker}) - ${file.ageInfo}`
          });
        }
      });

    // Sort by participant first, then by age
    return frequencyData.sort((a, b) => {
      if (a.participant !== b.participant) {
        return a.participant.localeCompare(b.participant);
      }
      return a.ageInDays - b.ageInDays;
    });
  }, [selectedItem, frequencyFiles, selectedFiles]);

  // Calculate stats
  const stats = useMemo(() => {
    return {
      totalEntries: allData.length,
      uniqueWords: new Set(allData.map(item => item.Word)).size,
      uniqueLemmas: new Set(allData.map(item => item.Lemma)).size
    };
  }, [allData]);

  const handleSelectItem = (item) => setSelectedItem(item);
  const handleCloseDetail = () => setSelectedItem(null);

  if (loading) return <LoadingView />;
  if (error) return <div className="p-6 text-red-600">Error loading data: {error}</div>;

  return (
    <div className="h-screen w-screen bg-gray-100 font-sans flex flex-col">
      <header className="bg-white border-b border-gray-200 p-4 shrink-0">
        <h1 className="text-xl font-bold text-gray-800">Georgian Corpus Data Explorer</h1>
      </header>

      <div className="flex-grow flex flex-col md:flex-row overflow-hidden">
        <div className="w-full md:w-1/3 lg:w-1/4 xl:w-1/5 shrink-0 h-full flex flex-col">
          <ControlPanel
            searchTerm={searchTerm}
            setSearchTerm={setSearchTerm}
            searchBy={searchBy}
            setSearchBy={setSearchBy}
            filters={filters}
            setFilters={setFilters}
            availableFiles={availableFiles}
            selectedFiles={selectedFiles}
            setSelectedFiles={setSelectedFiles}
          />
        </div>

        <div className="w-full md:w-2/3 lg:w-3/4 xl:w-4/5 h-full flex flex-col">
          {selectedItem ? (
            <DetailView
              item={selectedItem}
              onClose={handleCloseDetail}
              frequencyData={calculateFrequencyData}
            />
          ) : searchTerm ? (
            <ResultsList
              results={filteredResults}
              onSelectItem={handleSelectItem}
              selectedItem={selectedItem}
              resultCount={filteredResults.length}
              searchBy={searchBy}
            />
          ) : (
            <WelcomeView
              stats={stats}
              fileCount={availableFiles.length}
            />
          )}
        </div>
      </div>
    </div>
  );
}
